const btnSwitch=document.querySelector("#btn-switch"),userTheme=localStorage.getItem("theme"),systemTheme=window.matchMedia("(prefers-color-scheme:dark)").matches,checkTheme=()=>{"dark"==userTheme||1==systemTheme?(document.documentElement.classList.add("dark"),localStorage.setItem("theme","dark")):(localStorage.setItem("theme","light"),document.documentElement.classList.contains("dark")&&(document.documentElement.classList.remove("dark"),localStorage.setItem("theme","dark")))},switchTheme=()=>{document.documentElement.classList.contains("dark")?(document.documentElement.classList.remove("dark"),localStorage.setItem("theme","light")):(document.documentElement.classList.add("dark"),localStorage.setItem("theme","dark"))};btnSwitch.addEventListener("click",(()=>{switchTheme()})),checkTheme();